console.log("ðŸ“„ Starting PDF fetch...");

  var url = "https://www.uscis.gov/sites/default/files/document/forms/i-9.pdf";

  return fetch(url)
    .then(function (res) {
      if (!res.ok) {
        throw new Error("Failed to fetch PDF: " + res.statusText);
      }
      return res.arrayBuffer();
    })
    .then(function (arrayBuffer) {
      return PDFLib.PDFDocument.load(arrayBuffer);
    })
    .then(function (pdfDoc) {
      console.log("âœ… PDF loaded");

      var form = pdfDoc.getForm();
      var fields = form.getFields();
      var fieldNames = [];

      for (var i = 0; i < fields.length; i++) {
        fieldNames.push(fields[i].getName());
      }

      console.log("ðŸ§¾ Fields found:", fieldNames);

      function safeSetText(name, value) {
        try {
          form.getTextField(name).setText(value);
          console.log('âœ… Set "' + name + '"');
        } catch (e) {
          console.warn('âš ï¸ Could not set "' + name + '":', e.message);
        }
      }

      // Replace with real field names after inspection
      safeSetText("lastName", "Sallusti");
      safeSetText("firstName", "David");
      safeSetText("address", "123 Main St");

      form.flatten();

      return pdfDoc.save();
    })
    .then(function (pdfBytes) {
      var base64 = uint8ToBase64(new Uint8Array(pdfBytes));
      console.log("âœ… PDF encoded to Base64");

      // Assign to output object
      output.base64PDF = base64;
      return output;
    })
    .catch(function (err) {
      console.error("âŒ Error:", err.message);
      output.error = err.message;
      return output;
    });

  function uint8ToBase64(uint8Array) {
    var binary = '';
    for (var i = 0; i < uint8Array.byteLength; i++) {
      binary += String.fromCharCode(uint8Array[i]);
    }
    return btoa(binary);
  }
